import React,{useState,useEffect} from "react"
import { useNavigate } from "react-router";
import {
    Flex,
    Box,
    Stack,
    VStack,
    Text,
    FormControl,
    FormLabel,
    Input,
    InputGroup,
    InputLeftElement,
    Link,
    Button,
    Textarea,
    Divider
} from "@chakra-ui/react";

import{
    ArrowRightIcon,
    LockIcon,
} from "@chakra-ui/icons"

const Diary=()=>{

    const [myDiary,setDiary] = useState([]);
    const [message,setMessage] = useState("");
    const [loading,setLoading] = useState("hidden");

    const monthNames = ["January", "February", "March", "April", "May", "June","July", "August", "September", "October", "November", "December"];

    const getDate=()=>{
        let dateObj = new Date();
        let month = monthNames[dateObj.getMonth()];
        let h = dateObj.getHours();
        let m = dateObj.getMinutes();
        let day = String(dateObj.getDate()).padStart(2, '0');
        let year = dateObj.getFullYear();
        let ampm=" am";
        if(h>=12)ampm=" pm";

        let output = month  + ' '+ day  + ',' + year+' '+(h+12)%13+':'+m+ampm;
        return output;
    }

    const getMessage=(e)=>{
        setMessage(e.target.value);
    }

    const Clear=()=>{
        setMessage("");
    }

    const Post=(date)=>{  
        setLoading("block");  
        setDiary(
            oldDiary => [...oldDiary, [date,message]]
        );
    }

    useEffect(() => {
        setLoading("hidden");
     }, [myDiary]);

    return(
        <Flex 
          direction="column"
          w="100vw"
          align="center"
          justify="center"
          p={10}
          bgGradient="linear(360deg, #232A33 ,black)"
        >
            <Textarea
                w="full"
                h="70vh"
                bg="#292C2D"
                value={message}
                focusBorderColor="#292C2D"
                border="none"
                _focus={{boxShadow:"0px 5px 5px black"}}
                onChange={getMessage}
                boxShadow="0px 5px 5px black"
                fontSize="2xl"
                textColor="white"
                p={10}
                resize="none"
            />

           <Flex
            direction="row"
            bgGradient="linear(#676579,#01101a)"
            align="center"
            justify="space-evenly"
            boxShadow="0px 5px 5px black"
            w="45%"
            h="10vh">

            <Button
             w="15%"
             bgGradient="linear(green.500,green.700)"
             boxShadow="0px 5px 5px black" 
             _hover={{bgGradient:"linear(green.700,green.800)"}}
             onClick={()=>{Post(getDate())}}
            >
                <Text
                fontSize="sm"
                textColor="white"
                >
                    Post
                </Text>

            </Button>

            <Button
             w="15%"
             bgGradient="linear(yellow.300,yellow.500)"
             boxShadow="0px 5px 5px black" 
             _hover={{bgGradient:"linear(yellow,yellow.700)"}}
             onClick={Clear}
             mx={3}
            >
                <Text
                fontSize="sm"
                textColor="black"
                >
                    Clear
                </Text>

            </Button>

            <Button
             w="15%"
             bgGradient="linear(red.800,red.700)"
             boxShadow="0px 5px 5px black" 
             _hover={{bgGradient:"linear(red.800,red.500)"}}
             mx={3}
            >
                <Text
                fontSize="sm"
                textColor="black"
                >
                    Exit
                </Text>

            </Button>
        </Flex>

        <Divider w="75%" mt={10}/>

        <VStack
          spacing={6}
          w="75%"
        >
            {
                myDiary.map((key,entry)=> {return(
                    <Box
                     w="full"
                     h="20vh"
                     bgColor="gray"
                    >
                        <Text 
                         fontSize="2xl"
                         textColor="black"
                         >
                             {entry[0]}
                         </Text>
                    </Box>
                )})
            }

        </VStack>


        </Flex>
    )
}

export default Diary;
